    { ...msg, topic: `SELECT * FROM view_all_readings WHERE id = ?;`, payload: [id], readings: `all` },
    { ...msg, topic: `SELECT * FROM view_distance_readings WHERE id = ?;`, payload: [id], readings: `distance` },
];

const { payload: { all: readings, distance: distances } } = msg;

const canonOne = ({
    id, device_id, heartbeat, reported_at,

    accel_x, accel_y, accel_z,
    heading,
    zone,
    latitude: lat, longitude: lng,
    speed,
    temp_degrees: tmp,
    volume_db: vol,
}) => {
    const ds = distances
        .filter((d) => d.id === id)
        .map(({distance_from, distance}) => ({distance_from, distance}));

    return {
        id,
        device_id,
        heartbeat,
        reported_at,
        acceleration: accel_x === null
            ? undefined
            : { x: accel_x, y: accel_y, z: accel_z },
        distances: ds.length ? ds : undefined,
        heading: heading === null
            ? undefined
            : heading,
        location: (lat === null && lng === null && zone === null)
            ? undefined
            : {
                latitude: lat === null ? undefined : lat,
                longitude: lng === null ? undefined : lng,
                zone: zone === null ? undefined : zone,
            },
        speed: speed === null
            ? undefined
            : speed,
        temperature: tmp === null ? undefined : tmp,
        volume: vol === null ? undefined : vol,
    };
}

const res = readings.map(canonOne);
if (res.length === 0) {
    return {...msg, statusCode: 404}
} else if (res.length > 1) {
    return {...msg, statusCode: 500}
}
return { ...msg, payload: res[0] };
